class Solution {
public:
    int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return helper(nums, INT_MIN, INT_MAX, 0);
    }

    int helper(vector<int> &nums, int arr1, int arr2, int begin) {
        if (begin == nums.size()) {
            return 1;
        }

        if (arr1 != INT_MIN && arr2 != INT_MAX && dp[begin][arr1][arr2] != -1) {
            return dp[begin][arr1][arr2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[begin]; i++) {
            int curarr1 = i;
            int curarr2 = nums[begin] - i;

            if (curarr1 < arr1 || curarr2 > arr2) {
                continue;
            }

            ans = (ans + helper(nums, curarr1, curarr2, begin + 1)) % mod;
        }

        if (begin == 0) {
            return ans;
        }

        return dp[begin][arr1][arr2] = ans;
    }
};
