class Solution {
public:
    const int MOD = 1e9 + 7;

    int solve(int i, int p1, int p2, vector<int>& nums, vector<vector<vector<int>>>& dp) {
        if (i == nums.size()){
            return 1;
        }



        if (dp[i][p1][p2] != -1){
             return dp[i][p1][p2];
        } 



        int cnt = 0;
        for (int j = p1; j <= nums[i]; ++j) {
            int a2 = nums[i] - j;
            if (a2 <= p2) {
                cnt = (cnt + solve(i + 1, j, a2, nums, dp)) % MOD;
            }
        }

        return dp[i][p1][p2] = cnt;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));


        return solve(0, 0, 50, nums, dp);
    }
};
