class Solution {
public:
    int dp[2000][51][51];
    int N = 1e9 + 7;
    int func(vector<int> &nums, int i, int last1, int last2) {
        if(i==nums.size()) return 1;
        if(dp[i][last1][last2] != -1) return dp[i][last1][last2]%N;
        int ans = 0;
        for(int j=0; j<= nums[i];j++) {
            if((j>=last1) && ((nums[i]-j) <= last2)) {
                ans = ((ans%N) + (func(nums,i+1,j,nums[i]-j) % N))%N;
            }
        }
        return dp[i][last1][last2] = ans%N;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return func(nums,0,0,50)%N;
    }
};
