class Solution {
    long m=(long)1e9+7;
    public int countOfPairs(int[] nums) {
        long ans = solve(nums,0,51,0,new Long[52][52][nums.length+1]);
        return (int)ans;
    }
    long solve(int []arr,int one,int two,int n,Long [][][]dp)
    {
        if(n==arr.length)
            return 1;
        if(dp[one][two][n]!=null)
            return dp[one][two][n];
        long ans=0;
        for(int i=0;i<=arr[n];i++)
        {
            if(i>=one && (arr[n]-i)<=two)
            {
                ans=(ans%m+solve(arr,i,arr[n]-i,n+1,dp)%m)%m;
            }
        }
        return dp[one][two][n]= ans%m;
    }
}
