class Solution {
public:
    int dp[2002][52][52];
    int mod = 1e9 + 7;
    int memo(int i, int pA, int pB, vector <int> &nums) {
        if(i == (int) nums.size()) return 1;
        if(dp[i][pA + 1][pB + 1] != -1) return dp[i][pA + 1][pB + 1];
        int ways = 0;
        for(int j = 0; j < nums[i] + 1; j++) {
            if(pA == -1 or pA <= j) {
                int r = nums[i] - j;
                if(pB == -1 or pB >= r) {
                    ways += memo(i + 1, j, r, nums);
                    ways %= mod;
                }
            }
        }
        return dp[i][pA + 1][pB + 1] = ways % mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return memo(0, -1, -1, nums);
    }
};
