class Solution {
public:

    int mod=1e9+7;

    int geti(int i,int a,int b,vector<int>& A,vector<vector<vector<int>>> &dp) {

        if(i==A.size()) return 1;

        if(dp[i][a][b]!=-1) return dp[i][a][b];
        int cnt=0;

        for(int x=a;x<=A[i];x++) {
            int y=A[i]-x;
            if(y<=b) {
                cnt=(cnt+geti(i+1,x,y,A,dp))%mod;
            }
        }

        return dp[i][a][b]=cnt;
    }

    int countOfPairs(vector<int>& A) {

        int n=A.size();
        vector<vector<vector<int>>>  dp(n+4,vector<vector<int>> (55,vector<int> (55,-1)));
        return geti(0,0,50,A,dp);

    }
};
