class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = int(1e9) + 7
        n = len(nums)
        max_val = max(nums)

        dp = [[0] * (max_val + 1) for _ in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1 

        for i in range(1, n):
            for j in range(nums[i] + 1):
                total = 0
                for k in range(j + 1):
                    if k <= nums[i-1] - (nums[i] - j):
                        total = (total + dp[i-1][k]) % MOD
                dp[i][j] = total
        result = sum(dp[n-1][j] for j in range(nums[n-1] + 1)) % MOD
        return result
