class Solution {
public:

    int dp[2001][51][51];

    int mod = 1e9 + 7;

    int dfs(int idx,int a,int b,vector<int>& nums){
        if(idx == nums.size())
            return 1;
        if(dp[idx][a][b] != -1)
            return dp[idx][a][b];
        int res = 0;
        for(int i = 0;i<=nums[idx];i++){
            int o = nums[idx] - i;
            if((b >= o && a <= i) || idx == 0){
                res = (res + dfs(idx+1,i,o,nums))%mod;
            }
        }
        return dp[idx][a][b] = res;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return dfs(0,0,0,nums);
    }
};
