class Solution {
public:
    #define ll long long
    ll hell = 1e9+7;
    ll dp[2001][60];
    int f(int i,int pre,vector<int>&v){
        int n = v.size();
        if(i==n){
            return 1;
        }
        if(dp[i][pre+1]!=-1)return dp[i][pre+1];
        ll ways = 0;
        if(i==0){
            for(int j=0;j<=v[i];j++){
                ways = (ways+f(i+1,j,v))%hell;
            }
        }else{
            for(int j=0;j<=v[i];j++){
                if(j>=pre && v[i]-j<=v[i-1]-pre)
                    ways = (ways + f(i+1,j,v))%hell;
            }
        }

        return dp[i][pre+1]=ways;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return f(0,-1,nums);
    }
};
