class Solution {
public:
const int mod=1e9 + 7;
int dp[2001][52][52];
   int solve(int idx,int a,int b,vector<int>& nums){
     int n=nums.size();
    if(idx==n)return 1;
    if(dp[idx][a][b]!=-1)
    return dp[idx][a][b];
   int sum=0;
    for(int i=a;i<=nums[idx];i++){
        if(((nums[idx]-i)<=b))
       sum+=solve(idx+1,i,nums[idx]-i,nums);
       sum%=mod;
    }
    return dp[idx][a][b]=sum;
   }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return solve(0,0,51,nums);
    }
};
