class Solution {
public:
    #define ll long long int
    #define mod 1000000007
    int n;
    ll rec(int i, int k, vector<int>&a, vector<vector<ll>>&dp){
        if(i==n)return 1;
        if(dp[i][k]!=-1)return dp[i][k];

        int lastA=k, lastB=a[i-1]-k, ans=0;
        for(int x=lastA;x<=50;x++)if(a[i]-x>=0 && a[i]-x<=lastB)ans=(ans+rec(i+1,x,a,dp))%mod;
        return dp[i][k] = ans;
    }
    int countOfPairs(vector<int>&a) {
        n=a.size();
        ll ans=0;
        vector<vector<ll>>dp(n,vector<ll>(51,-1));
        for(int k=0;k<=50;k++)if(a[0]-k>=0)ans=(ans+rec(1,k,a,dp))%mod;
        return ans;
    }
};
