class Solution {
public:
    int solveRec(int ind, vector<int> &nums,int last1, int last2,vector<vector<vector<int>>> &dp){
        if(ind == nums.size())
            return 1;
        int ans = 0;
        int m = 1e9 + 7;

        if(dp[ind][last1][last2] != -1)
            return dp[ind][last1][last2];

        for(int i = 0;i <= nums[ind]; i++){
            int num1 = i;
            int num2 = nums[ind] - i;
            bool valid1 = last1 <= num1;
            bool valid2 = last2 >= num2; 
            if(valid1 && valid2){
                ans += solveRec(ind + 1,nums,num1,num2,dp);
                ans %= m;
            }
        }

        return dp[ind][last1][last2] = ans%m;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n + 1,vector<vector<int>> (51, vector<int> (53,-1)));
        return solveRec(0,nums,0,52,dp);   
    }
};
