class Solution {
public:
    int mod = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(vector<int> &nums, int index, int prev1, int prev2) {
        if (index >= nums.size()) {
            return 1;
        }
        if (dp[index][prev1+1][prev2+1] != -1) {
            return dp[index][prev1+1][prev2+1];
        }

        int ans = 0;
        for (int i = 0; i <= nums[index]; i++) {
            int up = i;
            int down = nums[index] - i;
            if ((prev1 == -1 || up >= prev1) && (prev2 == -1 || down <= prev2)) {
                ans = (ans + solve(nums, index + 1, up, down)) % mod;
            }
        }
        return dp[index][prev1+1][prev2+1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(101, vector<int>(101, -1)));
        return solve(nums, 0, -1, -1);
    }
};
