class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<int> cnt(51, 0);
        for (int i = 0; i <= nums[0]; i++)
            cnt[i] = 1;
        for (int i = 1; i < nums.size(); i++) {
            vector<int> cur(51, 0);
            int tot = 0;
            int diff = 0;
            diff = std::max(diff, nums[i] - nums[i-1]);
            for (int j = diff; j <= nums[i]; j++) {
                tot += cnt[j - diff];
                tot %= 1000000000 + 7;
                cur[j] = tot;
            }
            cnt = std::move(cur);
        }

        int ans = 0;
        for (int i = 0; i <= 50; i++) 
            ans = (ans + cnt[i]) % (1000000000 + 7);
        return ans;
    }
};
