#define ll long long
ll mod = 1e9 + 7;


ll dp[2001][53][53];

ll find(int pos, int p1, int p2, vector<int> &nums)
{

    if(pos == nums.size())
        return 1;

    ll curr = nums[pos];

    ll ans = 0;

    if(dp[pos][p1+1][p2] != -1)
        return dp[pos][p1+1][p2];

    for(int i=0; i<=curr; ++i)
    {
        ll cp1 = i;
        ll cp2 = curr-i;

        if(cp1>=p1 && cp2<=p2)
        {
            ans += find(pos+1, cp1, cp2, nums);  
            ans %= mod;
        }
    }

    ans %= mod;
    return dp[pos][p1+1][p2] = ans;
}


class Solution {
public:
    int countOfPairs(vector<int>& nums) {

        memset(dp, -1, sizeof(dp));
        ll ans = find(0, -1, 51, nums);
        return ans;    

    }
};
