class Solution {
public:
    int countPairs(int idx, int start, vector<int> &arr, vector<vector<long long>> &dp)
    {
        if (idx == arr.size())
            return 1;
        if (dp[idx][start] != -1)
            return dp[idx][start];

        int prev1 = (idx - 1 >= 0) ? start : 0;
        int prev2 = (idx - 1 >= 0) ? arr[idx - 1] - start : INT_MAX;

        long long ans = 0, mod = 1e9 + 7;
        for (int value = start; value <= arr[idx]; value++)
        {
            int curr1 = value;
            int curr2 = arr[idx] - value;
            if (prev2 >= curr2)
            {
                ans += countPairs(idx + 1, value, arr, dp);
                ans %= mod;
            }
        }

        return dp[idx][start] = ans;
    }

    int countOfPairs(vector<int> &arr)
    {
        int maxi = *max_element(arr.begin(), arr.end());
        vector<vector<long long>> dp(arr.size(), vector<long long>(maxi + 1, -1));
        return countPairs(0, 0, arr, dp);
    }
};
