class Solution {

    private static final int MOD = (int)1e9+7;
    private Map<String, Integer> memo;

    public int countOfPairs(int[] nums) {
        memo = new HashMap<>();

        return solve(0, 0, nums[0],nums);
    }

    public int solve(int i, int prev1, int prev2,int []nums) {
        if (i == nums.length) {
            return 1;
        }

        String key = i + "," + prev1 + "," + prev2;
        if (memo.containsKey(key)){
            return memo.get(key);
        }

        int count = 0;
        for (int j = prev1; j <= nums[i]; j++) {
            int k = nums[i] - j;
            if (k <= prev2) {
                count = (count + solve(i + 1, j, k,nums)) % MOD;
            }
        }

        memo.put(key, count);
        return count;
    }
}
