class Solution {
public:

    #define ll long long
    const int mod = 1e9+7;

    int countOfPairs(vector<int>& a) {
        ll n = a.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>> (52, vector<ll> (52, -1)));
        auto fn = [&] (ll i, ll prev1, ll prev2, auto &&fn) -> ll {
            if(i == n) {
                return 1;
            }
            if(dp[i][prev1][prev2] != -1) {
                return dp[i][prev1][prev2];
            }
            if(prev1 == 51) {
                ll ans = 0;
                for(int j = 0; j <= a[i]; j++) {
                    ans += fn(i + 1, j, a[i] - j, fn);
                }
                ans %= mod;
                return dp[i][prev1][prev2] = ans;
            }
            ll ans = 0;
            for(int j = 0; j <= a[i]; j++) {
                if(j >= prev1 && a[i] - j <= prev2)
                    ans += fn(i + 1, j, a[i] - j, fn);
            }
            ans %= mod;
            return dp[i][prev1][prev2] = ans;
        };
        return fn(0,51,51,fn);
    }
};
