class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> dp;

    int solve(vector<int>& nums, int i, int prev1, int prev2) {
        if (i == nums.size()) {
            return 1;
        }

        if (dp[i][prev1][prev2] != -1) {
            return dp[i][prev1][prev2];
        }

        int res = 0;
        for (int prev_i = prev1; prev_i <= nums[i]; ++prev_i) {
            int arr2_i = nums[i] - prev_i;
            if (arr2_i <= prev2) {
                res = (res + solve(nums, i + 1, prev_i, arr2_i)) % MOD;
            }
        }

        return dp[i][prev1][prev2] = res;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, 50);
    }
};
