int dp[2002][52];

class Solution {

    const int M = 1e9 + 7;

    int solve(vector<int> &nums, int pos, int prev1){
        if(pos == nums.size())
            return 1;

        if(dp[pos][prev1] != -1)
            return dp[pos][prev1];

        int prev2 = pos == 0 ? INT_MAX : nums[pos-1] - prev1;

        int count = 0;

        for(int first = prev1; first <= nums[pos]; first++){
            int second = nums[pos] - first;
            if(second <= prev2){
                count = (count + (long long)solve(nums, pos + 1, first)) % M;
            }
        }

        dp[pos][prev1] = count;
        return count;
    }

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        for(int i=0; i<=n; i++)
            for(int j=0; j<=50; j++)
                dp[i][j] = -1;

        return solve(nums, 0, 0);

    }
};
