class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)

        dp = {}

        def solve(index, prev1, prev2):
            if index == n: return 1

            if (index, prev1, prev2) in dp: return dp[(index, prev1, prev2)]
            res = 0
            t = nums[index]
            for i in range(prev1, 51):
                if t - i >= 0 and t - i <= prev2:
                    res = (res + solve(index + 1, i, t - i))%(int(1e9) + 7)

            dp[(index, prev1, prev2)] = res
            return res%(int(1e9) + 7)
        return solve(0, 0, 50)
