class Solution {
public:

    int mod =1e9+7;

    int solve( vector<vector<int>> &dp,int i,vector<int>& nums,int prev1){

        int n = nums.size();
        if(i==n){
            return 1;
        }

        if(dp[i][prev1+1]!=-1){
            return dp[i][prev1+1];
        }

        int ans =0;


        for(int j=0;j<=nums[i];j++){
            int prev2=0;
                if(i>0)
             prev2 =nums[i-1]-prev1;
             else
             prev2=1e9;
           if(j>=prev1&&nums[i]-j<=prev2){
            ans=(ans+solve(dp,i+1,nums,j))%mod;
           }
        }


        return dp[i][prev1+1]=ans;


    }
    int countOfPairs(vector<int>& nums) {

        int n = nums.size();
        vector<vector<int>> dp(n,vector<int>(52,-1));

        return solve(dp,0,nums,-1);
    }
};
