class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = int(10**9 + 7)
        # res = 0
        n = len(nums)
        @cache
        def cal(i, prev1, prev2):
            if i == n:
                return 1
            # nonlocal res
            res = 0
            for val1 in range(prev1, nums[i]+1):
                val2 = nums[i] - val1
                if val2 <= prev2:
                    res += cal(i+1, val1, val2)


            return res % mod

        return cal(0, 0, float("inf"))
