#include <vector>
#include <cstring>

class Solution {
public:
    int d = 1e9 + 7;
    int a[2001][51][51];

    int b(int c, int e, int f, vector<int>& g) {
        if (c == g.size()) {
            return 1;
        }

        if (a[c][e][f] != -1) {
            return a[c][e][f];
        }

        int h = 0;

        for (int i = e; i <= g[c]; ++i) {
            int j = g[c] - i;
            if (j <= f) {
                h = (h + b(c + 1, i, j, g)) % d;
            }
        }

        return a[c][e][f] = h;
    }

    int countOfPairs(std::vector<int>& g) {
        memset(a, -1, sizeof(a));
        return b(0, 0, 50, g);
    }
};
