#define ll long long
ll mod = 1e9 + 7;
class Solution {
public:
    ll helper(ll index, ll mx, ll mn, vector<vector<vector<ll>>> &dp, vector<int> &nums){
        if(index < 0)
            return 1;
        if(dp[index][mx][mn] != -1)
            return dp[index][mx][mn];
        dp[index][mx][mn] = 0;
        for(ll i = 0; i <= min(mx, (ll)nums[index]); i++){
            ll val = nums[index] - i;
            if(val >= mn)
                dp[index][mx][mn] = (dp[index][mx][mn] + helper(index - 1, i, val, dp, nums)) % mod;
        }
        return dp[index][mx][mn];
    }

    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return (int)(helper(n - 1, 50, 0, dp, nums) % mod);
    }
};
