class Solution {
public:
    const int mod = 1e9 + 7;
    int dp[2001][53][53];
    int sol(int i , int prev1 , int prev2 , vector<int> &nums){
        int n = nums.size();
        if(i >= n){
            return 1;
        }
        int x , y;
        long long ans = 0;
        if(dp[i][prev1+1][prev2] != -1) return dp[i][prev1+1][prev2];
        for(int k = 0 ;k <= nums[i];k++){
            x = k;
            y = nums[i] - x;
            if(x >= prev1 && y <= prev2){
                ans = ans + sol(i + 1 , x , y , nums);
            }
        }
         return dp[i][prev1+1][prev2] = ans % mod;

    }
    int countOfPairs(vector<int>& nums) {
        memset(dp , -1 , sizeof(dp));
        return sol(0 , -1 , 51, nums);
    }
};
