class Solution {
    int mod = 1e9+7;
    const int bt(vector<vector<vector<int>>>& memo,
        vector<int>& nums, int index, int prevone, int prevtwo)
    {
        if(index >= nums.size())
            return 1;

        if(memo[index][prevone][prevtwo] != -1)
            return memo[index][prevone][prevtwo];

        memo[index][prevone][prevtwo] = 0;

        // fix arr 1 value
        for(int v{prevone}; v <= nums[index]; ++v)
        {
            int one = v;
            int two = nums[index]-one;

            if(two > prevtwo)
                continue;

            memo[index][prevone][prevtwo] = 
                (memo[index][prevone][prevtwo] + bt(memo,nums,index+1,one,two)) % mod;
        }

        return memo[index][prevone][prevtwo];
    }
public:
    int countOfPairs(vector<int>& nums) {
        // arr1 equal or more
        // arr2 equal or lesser
        // same index sum is nums index

        vector<vector<vector<int>>> memo(nums.size(),vector<vector<int>>(51,vector<int>(51,-1)));
        return bt(memo,nums,0,0,50);
    }
};
