class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @lru_cache(None)
        def dfs(i, prev1, prev2):
            if i == n:
                return 1

            ans,cursum = 0,nums[i]


            for arr1_i in range(prev1, cursum + 1):
                arr2_i = cursum - arr1_i

                if arr2_i <= prev2:
                    ans += dfs(i + 1, arr1_i, arr2_i)
                    ans %= MOD

            return ans

        return dfs(0, 0, nums[0])
