class Solution {
    vector<int> arr;
    int n;
    vector<vector<int>> dp;
    int mod=1e9+7;
public:

    int rec(int i,int prev){
        if(i==n){
            return 1;
        }

        if(dp[i][prev]!=-1)return dp[i][prev];

        int ans=0;
        if(i==0){
            for(int j=0;j<=arr[i];j++){
                ans=(ans+rec(i+1,j))%mod;
            }
        }
        else{
            int ot=arr[i-1]-prev;
            for(int j=prev;j<=arr[i];j++){
                if(arr[i]-j<=ot)
                ans=(ans+rec(i+1,j))%mod;
            }
        }
        return dp[i][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        arr=nums;
        dp.assign(n+1,vector<int>(52,-1));
        return rec(0,0);
    }
};
