class Solution {
public:
    int mod=1000000007;
    int rec(int inx,int arr1prev,vector<int>&nums,vector<vector<int>>&dp){
        if(inx==nums.size()) return 1;
        if(dp[inx][arr1prev]!=-1) return dp[inx][arr1prev];
        int arr2prev=(inx-1>=0)?(nums[inx-1]-arr1prev):1e2;
        int ans=0;
        for(int e1=arr1prev;e1<=nums[inx];e1++){
            int e2=(nums[inx]-e1);
            if(arr2prev>=e2){
                ans=(ans%mod + rec(inx+1,e1,nums,dp)%mod)%mod;
            }
        }
        return dp[inx][arr1prev]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>dp(nums.size(),vector<int>(51,-1));
        return rec(0,0,nums,dp);
    }
};
