class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def dfs(ptr, pre1, pre2):
            if ptr == n:
                return 1
            ans = 0
            for num in range(nums[ptr], pre1 - 1, -1):
                if nums[ptr] - num > pre2:
                    break
                ans = (ans + dfs(ptr + 1, num, nums[ptr] - num)) % mod
            return ans

        mod = 10 ** 9 + 7
        n = len(nums)
        return dfs(0, 0, inf)
