class Solution {

    int MOD = (int)(1e9+7);
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[51][n];


        for(int[] d : dp){
            Arrays.fill(d,-1);
        }

        return rec(0,0,nums,dp);   
    }

    private int rec(int p,int i,int[] nums,int[][] dp){
        if(i >= nums.length) return 1;

        if(dp[p][i] != -1) return dp[p][i];
         int pm = (i == 0) ? 0 : nums[i-1] - p;

        int res = 0;
        for(int np = p;np <= nums[i] ;np++){

            int npm = nums[i] - np;

            if(pm >= npm || i == 0){

                int resr = rec(np,i+1,nums,dp);

                res = (resr + res) % MOD;
            }



        }        

        return dp[p][i] = res;

    }

}
