#define mod 1000000007
#define ll long long
class Solution {
public:
    ll func(int i,int prev,vector<int>& nums,vector<vector<int>>& dp)
    {
        if(i==nums.size()) return 1;
        if(dp[i][prev]!=-1) return dp[i][prev];
        ll ans=0;
        for(int j=prev;j<=nums[i];j++)
        {
            ll rem=i>0?nums[i-1]-prev:INT_MAX;
            ll remcurr=nums[i]-j;
            if(rem>=remcurr) ans+=func(i+1,j,nums,dp);
            ans%=mod;
        }
        return dp[i][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(nums.size()+1,vector<int>(51,-1));
        return (int) func(0,0,nums,dp);
    }
};
