class Solution {
public:
    long long n, dp[2001][61], mod = 1e9+7;
    long long rec( int i, int l1, int l2, vector<int> &nums ){
        if ( i == n ) return 1;
        if ( dp[i][l1] != -1 ) return dp[i][l1];
        long long ans = 0;
        for ( int k = 0; k<=nums[i]; k++ ){
            if ( k >= l1 && nums[i]-k <= l2 ){
                ans += rec(i+1, k, nums[i]-k, nums)%mod;
            }
        } return dp[i][l1] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp)); n = nums.size();
        return rec(0, 0, 50, nums);
    }
};
