class Solution {
public:
    vector<vector<vector<int>>> v;
    vector<int> arr;
    int mod = 1e9+7;
    int func(int i,int a,int b)
    {
        if(i==arr.size())
            return 1;
        if(v[i][a][b]!=-1)
            return v[i][a][b];
        long long ans = 0;
        for(int j=0;j<=arr[i];j++)
        {
            if(j>=a&&(arr[i]-j)<=b)
            {
                ans+= func(i+1,j,arr[i]-j);
                ans%= mod;
            }
        }
        return v[i][a][b] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        arr = nums;
        v.resize(nums.size(),vector<vector<int>> (52,vector<int>(52,-1)));
        return func(0,0,50);
    }
};
