class Solution {
public:
    const int mod = 1e9 + 7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector dp(n + 1, vector<int>(51));
        for (int i = 0; i <= nums[0]; ++i)
            dp[1][i] = 1;

        for (int i = 2; i <= n; ++i)
            for (int j = 0; j <= nums[i - 1]; ++j)
                for (int k = j; k <= nums[i - 2]; ++k) 
                    if (nums[i - 1] - j >= nums[i - 2] - k)
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % mod;

        int ans = 0;
        for (int i = 0; i <= nums.back(); ++i)
            ans = (ans + dp[n][i]) % mod;

        return ans;
    }
};
