ll mod = 1e9 + 7;
class Solution {
public:
    ll dp[2001][55][55];
    ll solve(int idx,vi &nums,int last1,int last2)
    {
        if(idx>=nums.size())
        {
            return 1;
        }
        ll ans = 0;
        ll &t=dp[idx][last1+1][last2];
        if(t!=-1) return t;
        fori(i,0,nums[idx]+1)
        {
            int j = nums[idx]-i;
            if(i>=last1 && j<=last2)
            {
                ans+=solve(idx+1,nums,i,j);
                ans%=mod;
            }
        }
        return t=ans;

    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(0,nums,-1,51);
    }
};
