class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] f = new long[n][51];
        f[0][0] = 1;
        long mod = (long)(1e9 + 7);
        for (int i = 0; i <= nums[0]; i++) {
            f[0][i] = 1;
        }
        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                for (int k = j; k >= 0; k--) {
                    if ((nums[i] - j <= nums[i - 1] - k)) {
                        f[i][j] = (f[i][j] +  f[i - 1][k]) % mod;
                    }
                }
            }
        }
        long ans = 0;
        for (int j = 0; j <= 50; j++) {
            ans = (ans + f[n - 1][j]) % mod;
        }
        return (int)ans;
    }
}
