class Solution {
public:

    const int mod = 1e9+7;
    int dp[2020][55][55];
    int n;

    int recur(int ind, int lastA, int lastB, vector<int>& nums){
        if(ind == n)return 1;

        if(dp[ind][lastA][lastB] != -1)return dp[ind][lastA][lastB];

        int ans = 0;

        for(int i = lastA; i<= nums[ind]; i++){
            if(nums[ind] - i <= lastB){
                ans = (ans% mod + recur(ind+1, i, nums[ind] - i, nums)%mod)%mod;
            }
        }
        return dp[ind][lastA][lastB] = ans;


    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();  
        for(int i=0; i<n+5; i++){
            for(int j=0; j<55; j++){
                for(int k=0; k<55; k++)dp[i][j][k] = -1;
            }
        }  

        return recur(0, 0, 51, nums);
    }
};
