from functools import lru_cache

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        ans = 0
        @lru_cache(None)
        def dp(start1, start2, i):
            res = 0
            if i == len(nums):
                return 1
            for item1 in range(max(start1, nums[i]-start2), nums[i]+1):
                res += dp(item1, nums[i]-item1, i+1)
            return res % 1000000007
        for s1 in range(nums[0]+1):
            ans += dp(s1, nums[0]-s1, 1)
            ans = ans % 1000000007
        return ans
