class Solution:
    def countOfPairs(self, nums: List[int]) -> int:


        MOD = 10**9 + 7
        n = len(nums)

        memo = {}

        def dfs(index, last_a, last_b):
            if index == n:
                return 1

            if (index, last_a, last_b) in memo:
                return memo[(index, last_a, last_b)]

            total_count = 0
            for a in range(last_a, nums[index] + 1):
                b = nums[index] - a
                if b <= last_b:
                    total_count += dfs(index + 1, a, b)
                    total_count %= MOD

            memo[(index, last_a, last_b)] = total_count
            return total_count

        return dfs(0, 0, 50)
