class Solution(object):
    def countOfPairs(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        n = len(nums)
        MOD = 10**9 + 7

        def count_ways(index, prev1, prev2, memo):
            if index == n:
                return 1
            if (index, prev1, prev2) in memo:
                return memo[(index, prev1, prev2)]

            count = 0
            for i in range(prev1, nums[index] + 1):
                j = nums[index] - i
                if j <= prev2:
                    count = (count + count_ways(index + 1, i, j, memo)) % MOD

            memo[(index, prev1, prev2)] = count
            return count

        return count_ways(0, 0, float('inf'), {})
