class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int MOD = 1_000_000_007;
        int[][] dp = new int[n][51];


        for (int j = 0; j <= nums[0]; j++) {
            dp[0][j] = 1;
        }


        for (int i = 1; i < n; i++) {
            int[][] newDp = new int[n][51];
            for (int j = 0; j <= nums[i]; j++) {
                int arr2Val = nums[i] - j;
                for (int k = 0; k <= j; k++) { 
                    if (arr2Val <= nums[i-1] - k) { 
                        newDp[i][j] = (newDp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
            dp = newDp;
        }


        int result = 0;
        for (int j = 0; j <= 50; j++) {
            result = (result + dp[n - 1][j]) % MOD;
        }

        return result;
    }
}
