from typing import List

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        @lru_cache(None)
        def dfs(index, prev1, prev2):
            if index == n:
                return 1
            count = 0
            for x in range(prev1, nums[index] + 1):
                y = nums[index] - x
                if y <= prev2:
                    count = (count + dfs(index + 1, x, y)) % MOD
            return count
        return dfs(0, 0, nums[0])
