class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        const int n = nums.size();
        const int max_val = 50; 

        long long memo[2000][51][51];
        memset(memo, -1, sizeof(memo));

        function<long long(int, int, int)> dp = [&](int index, int p1, int p2) -> int {
            if (index == n) {
                return 1;
            }

            if (memo[index][p1][p2] != -1) {
                return memo[index][p1][p2];
            }

            long long res = 0;

            for (int i = p1; i <= nums[index]; ++i) {
                int j = nums[index] - i;
                if (j <= p2) {
                    res = (res + dp(index + 1, i, j)) % MOD;
                }
            }

            memo[index][p1][p2] = res;
            return res;
        };

        return dp(0, 0, nums[0]);
    }
};
