class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        @lru_cache(None)
        def dp(i: int, prev1: int, prev2: int) -> int:
            if i == n:
                return 1

            total_pairs = 0

            for arr1_i in range(prev1, nums[i] + 1):
                arr2_i = nums[i] - arr1_i

                if arr2_i <= prev2:
                    total_pairs += dp(i + 1, arr1_i, arr2_i)
                    total_pairs %= MOD

            return total_pairs

        return dp(0, 0, nums[0])
