class Solution {
public:
    int mod = 1e9 + 7;
    long long solve(int i, long long prevnum1, long long prevnum2, vector<int>& nums, vector<vector<vector<long long>>>& dp){
        int n = nums.size();
        if(i == n){
            return 1;
        }
        if(dp[i][prevnum1][prevnum2]!= -1){
            return dp[i][prevnum1][prevnum2];
        }
        long long ans = 0;
        for(int j = 0; j<=nums[i]; j++){
            if(j >= prevnum1 && nums[i] - j <= prevnum2){
                ans += solve(i + 1, j, nums[i] - j, nums, dp);
                ans %= mod;
            }
        }
        return dp[i][prevnum1][prevnum2] =  ans;

    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>>dp(n, vector<vector<long long>>(60, vector<long long>(60, -1)));
        long long ans = solve(0, 0, 55, nums, dp);
        ans %= mod;
        return ans;
    }
};
