class Solution {
    private static final int MOD = 1000000007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] dp = new long[51][51];
        long[][] sum = new long[51][51];

        for (int x = 0; x <= nums[0]; x++) {
            dp[x][nums[0] - x] = 1;
        }

        for (int i = 1; i < n; i++) {
            sum[0][0] = dp[0][0];

            for (int x = 1; x <= 50; x++) {
                sum[x][0] = (sum[x - 1][0] + dp[x][0]) % MOD;
            }

            for (int y = 1; y <= 50; y++) {
                sum[0][y] = (sum[0][y - 1] + dp[0][y]) % MOD;
            }

            for (int x = 1; x <= 50; x++) {
                for (int y = 1; y <= 50; y++) {
                    sum[x][y] = (sum[x - 1][y] + sum[x][y - 1] - sum[x - 1][y - 1] + dp[x][y] + MOD) % MOD;
                }
            }

            long[][] newDp = new long[51][51];
            for (int x = 0; x <= nums[i]; x++) {
                int y = nums[i] - x;
                if (y <= 50) {
                    newDp[x][y] = sum[x][50] - (y > 0 ? sum[x][y - 1] : 0) + MOD;
                    newDp[x][y] %= MOD;
                }
            }
            dp = newDp;
        }

        long result = 0;
        for (int x = 0; x <= 50; x++) {
            for (int y = 0; y <= 50; y++) {
                result = (result + dp[x][y]) % MOD;
            }
        }
        return (int) result;
    }

    public static void main(String[] args) {
        Solution sol = new Solution();

        int[] nums1 = {2, 3, 2};
        System.out.println("Output: " + sol.countOfPairs(nums1));  // Expected Output: 4

        int[] nums2 = {5, 5, 5, 5};
        System.out.println("Output: " + sol.countOfPairs(nums2));  // Expected Output: 126
    }
}
