class Solution {
public:
    int n;
    vector<int>arr;
    #define int long long
    int dp[2010][51][51];
    int mod = 1e9 + 7;

    int rec(int level, int last1, int last2){
        if(level == n)return 1;
        if(dp[level][last1][last2]!=-1) return dp[level][last1][last2];
        int ans = 0;
        for(int i = arr[level]; i>=last1; i--){
            int j = arr[level] - i;
            if(i >= last1 && j<= last2){
                ans += rec(level+1, i, j)%mod;
                ans %= mod;
            }
        }
        return dp[level][last1][last2]=ans%mod;
    }
    #undef int
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        arr = nums;
        memset(dp,-1,sizeof(dp));
        return rec(0,0,50);
    }
};
