#define FOR(i, a, b) for (int i = (a); i < (b); i++)
#define REP(i, n) for (int i = 0; i < (n); i++)

const int M = 51, P = 1000000007;

class Solution {
public:
  int countOfPairs(vector<int>& a) {
    int n = a.size();
    vector<vector<int>> s(n, vector<int>(51));
    REP(j, a[0]+1)
      s[0][j] = 1;
    REP(i, n-1)
      REP(j, a[i]+1) {
        if (!s[i][j])
          continue;
        FOR(k, j, a[i+1]+1) {
          if (a[i]-j < a[i+1]-k)
            continue;
          (s[i+1][k] += s[i][j]) %= P;
        }
      }
    int ret = 0;
    REP(j, M)
      (ret += s[n-1][j]) %= P;
    return (ret%P+P)%P;
  }
};
